import { useState } from "react";


const DiaryEditor = () => {
    const[state, setstate] = useState(
        {
            author:" ",
            content:" ",
        }
    )
return(
<div className="DiartEditor">
    <h2>오늘의 일기</h2>
    <div>
    <input
    name ="author"
    value={state.author}
    onChange={(e) => {
        setstate({
            ...state,
            author:e.target.value,
            })
    }}></input>
    </div>
    <textarea
    name="content"
    value={state.content}
    onChange={(e)=>{
        setstate({
            ...state,
            content: state.content,
            })
    }

    }></textarea>
</div>
);
};

export default DiaryEditor;
