import { useState } from "react";


const DiaryEditor = () => {
    const[state, setstate] = useState(
        {
            author:" ",
            content:" ",
        }
    )

const handleChangeState = (e) => {
   setstate({
    ...state,
    [e.taget.name]: e.taget.value,
   });
}

return(
<div className="DiartEditor">
    <h2>오늘의 일기</h2>
    <div>
    <input
    name ="author"
    value={state.author}
    onChange={(e) => {
        setstate(handleChangeState)
    }}></input>
    </div>
    <textarea
    name="content"
    value={state.content}
    onChange={(e)=>{
        setstate(handleChangeState)
    }}></textarea>
</div>
);
};

export default DiaryEditor;
